import React, { useState } from "react";
import "../styling/FileUpload.css";

function FileUpload() {
  const [file, setFile] = useState(null);
  const [uploading, setUploading] = useState(false);
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");

  // ref to the file input element
  const fileInputRef = React.createRef();

  const handleDragOver = (e) => {
    e.preventDefault(); // prevents the default action associated with the event
    e.stopPropagation(); // stops the event from propagating (bubbling) to parent elements, preventing other event handlers from being triggered.
  };

  const handleDrop = (e) => {
    e.preventDefault();
    e.stopPropagation();
    const droppedFile = e.dataTransfer.files[0];
    if (droppedFile) {
      setFile(droppedFile);
      setError("");
      setSuccess("");
    }
  };

  const handleDoubleClick = (e) => {
    fileInputRef.current.click();
  };

  // Uploading process
  const handleUpload = () => {
    if (!file) {
      setError("Please select a file to upload.");
      return;
    }

    setUploading(true);
    setError("");
    setSuccess("");

    setTimeout(() => {
      setUploading(false);
      setSuccess("File uploaded successfully!");
    }, 2000);
  };

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0] ;
    if( selectedFile ){
        setFile(selectedFile) ;
        setError('') ;
        setSuccess('') ;
    }
  }

  return (
    <div className="file-upload-container">
      <h2>Upload Your File</h2>
      <div
        className="file-upload-box"
        onDragOver={handleDragOver}
        onDrop={handleDrop}
        onDoubleClick={handleDoubleClick}
      >
        <div className="file-upload-area">
          {file ? (
            <div className="file-info">
              <p>{file.name}</p>
            </div>
          ) : (
            <p>Drag & Drop or Click to select a File</p>
          )}
          <input
            type="file"
            className="file-input"
            onChange={handleFileChange}
            accept="image/*, .pdf, .xlsx"
            ref={fileInputRef}
          />
        </div>
        {error && <p className="error-message">{error}</p>}
        {success && <p className="success-message">{success}</p>}
      </div>
    </div>
  );
}

export default FileUpload;
