import React, { useState, useRef } from "react";
import { 
  CloudUploadOutlined, 
  FileTextOutlined, 
  FileExcelOutlined, 
  FilePdfOutlined, 
  FileImageOutlined, 
  CloseOutlined 
} from '@ant-design/icons';

function FileUpload() {
  const [files, setFiles] = useState([]);
  const [uploadProgress, setUploadProgress] = useState(0);
  const [error, setError] = useState("");
  const fileInputRef = useRef(null);

  const MAX_FILE_SIZE = 10 * 1024 * 1024; // 10MB
  const ALLOWED_TYPES = ['image/png', 'image/jpeg', 'application/pdf', 'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet'];

  const getFileIcon = (file) => {
    const fileType = file.type;
    if (fileType.includes('image/')) return <FileImageOutlined />;
    if (fileType === 'application/pdf') return <FilePdfOutlined />;
    if (fileType.includes('spreadsheet') || fileType.includes('excel')) return <FileExcelOutlined />;
    return <FileTextOutlined />;
  };

  const validateFiles = (selectedFiles) => {
    const validFiles = Array.from(selectedFiles).filter(file => {
      const isValidType = ALLOWED_TYPES.includes(file.type);
      const isValidSize = file.size <= MAX_FILE_SIZE;

      if (!isValidType) {
        setError(`Invalid file type: ${file.name}`);
      }
      if (!isValidSize) {
        setError(`File too large: ${file.name} (max 10MB)`);
      }

      return isValidType && isValidSize;
    });

    return validFiles;
  };

  const handleFileSelect = (e) => {
    const selectedFiles = e.target.files;
    const validFiles = validateFiles(selectedFiles);
    
    setFiles(prevFiles => [...prevFiles, ...validFiles]);
    setError("");
  };

  const handleDrop = (e) => {
    e.preventDefault();
    const droppedFiles = e.dataTransfer.files;
    const validFiles = validateFiles(droppedFiles);
    
    setFiles(prevFiles => [...prevFiles, ...validFiles]);
    e.target.classList.remove('drag-over');
  };

  const handleDragOver = (e) => {
    e.preventDefault();
    e.target.classList.add('drag-over');
  };

  const handleDragLeave = (e) => {
    e.target.classList.remove('drag-over');
  };

  const handleUpload = () => {
    if (files.length === 0) {
      setError("Please select files to upload.");
      return;
    }

    // Simulate upload progress
    let progress = 0;
    const uploadInterval = setInterval(() => {
      progress += 10;
      setUploadProgress(progress);

      if (progress >= 100) {
        clearInterval(uploadInterval);
        setFiles([]);
        setUploadProgress(0);
      }
    }, 200);
  };

  const removeFile = (fileName) => {
    setFiles(files.filter(file => file.name !== fileName));
  };

  return (
    <div className="file-upload-container">
      <div 
        className="drag-drop-zone"
        onDrop={handleDrop}
        onDragOver={handleDragOver}
        onDragLeave={handleDragLeave}
        onClick={() => fileInputRef.current.click()}
      >
        <CloudUploadOutlined className="upload-icon" />
        <p>Drag & Drop or Click to Upload Files</p>
        <input 
          type="file"
          ref={fileInputRef}
          onChange={handleFileSelect}
          multiple
          hidden
          accept={ALLOWED_TYPES.join(',')}
        />
      </div>

      {error && <div className="error-message">{error}</div>}

      {uploadProgress > 0 && (
        <div className="progress-bar">
          <div 
            className="progress" 
            style={{width: `${uploadProgress}%`}}
          ></div>
        </div>
      )}

      {files.length > 0 && (
        <div className="file-list">
          {files.map((file, index) => (
            <div key={index} className="file-item">
              {getFileIcon(file)}
              <span>{file.name}</span>
              <span className="file-size">
                {(file.size / 1024).toFixed(1)} KB
              </span>
              <button 
                className="remove-file" 
                onClick={() => removeFile(file.name)}
              >
                <CloseOutlined />
              </button>
            </div>
          ))}
        </div>
      )}

      <button 
        className="upload-btn" 
        onClick={handleUpload}
        disabled={files.length === 0 || uploadProgress > 0}
      >
        {uploadProgress > 0 ? `Uploading (${uploadProgress}%)` : 'Upload Files'}
      </button>
    </div>
  );
}

export default FileUpload;
