import './styling/App.css';
import logo from './assets/swipeLogo.png';
import CustomersTable from './components/CustomersTable';
import InvoiceTable from './components/InvoiceTable';
import ProductTable from './components/ProductTable';
import { useState } from 'react';
import { BrowserRouter as Router, Route, Link } from 'react-router-dom';

function App() {
  const [selectedTab, setSelectedTab] = useState('About');

  const renderTabContent = () => {
    switch (selectedTab) {
      case 'Customers':
        return <CustomersTable />;
      case 'Invoices':
        return <InvoiceTable />;
      case 'Products':
        return <ProductTable />;
      case 'About':
        return <div>About us content</div>;
      default:
        return <div>Welcome to the App</div>;
    }
  };

  return (
    <Router>
      <div className="App">
        <header className='header'>
          <img className='header-image' src={logo} alt='Logo' />
          <div className='header-buttons'>
            {/* Home Link using React Router */}
            <Link to="/" className='header-button'>
              Home
            </Link>
            <Link
              to="/invoices"
              className={`header-button ${selectedTab === 'Invoices' ? 'active' : ''}`}
              onClick={() => setSelectedTab('Invoices')}
            >
              Invoices
            </Link>
            <Link
              to="/products"
              className={`header-button ${selectedTab === 'Products' ? 'active' : ''}`}
              onClick={() => setSelectedTab('Products')}
            >
              Products
            </Link>
            <Link
              to="/customers"
              className={`header-button ${selectedTab === 'Customers' ? 'active' : ''}`}
              onClick={() => setSelectedTab('Customers')}
            >
              Customers
            </Link>
            <Link
              to="/about"
              className={`header-button ${selectedTab === 'About' ? 'active' : ''}`}
              onClick={() => setSelectedTab('About')}
            >
              About
            </Link>
          </div>
        </header>

        {/* Dynamic content */}
        <div className="content">
          <Switch>
            <Route exact path="/" render={() => <div>Home Page</div>} />
            <Route path="/invoices" render={() => <InvoiceTable />} />
            <Route path="/products" render={() => <ProductTable />} />
            <Route path="/customers" render={() => <CustomersTable />} />
            <Route path="/about" render={() => <div>About us content</div>} />
            {/* Default route if no match */}
            <Route render={() => <div>Welcome to the App</div>} />
          </Switch>
        </div>
      </div>
    </Router>
  );
}

export default App;
